        flex-direction: column;
        justify-content: center;
        align-items: center;
    }

    .header__nav {
        z-index: 3;
    }

    .nav__link {
        font-size: 82px;
        width: 100%;
    }

    .nav__link:not(:last-child) {
        margin-bottom: 55px;
    }

    .nav__logo {
        position: absolute;
        z-index: 1;
        opacity: .5;
    }
}

