    header {
        padding: 0 95px;
    }

    .nav__link {
        font-size: 62px;
    }
    
    .nav__hashtag {
        display: none;
    }
}

@media screen and (max-width: 550px) {
    header {
        padding: 0 35px;
        background-image: url('/feeb1d851f56005d54aee72991778f73.jpg');
        background-size: 50%;
        background-repeat: no-repeat;
        background-position: center center;
    }

    .nav__link {
        font-size: 32px;
    }

    .nav__link:not(:last-child) {
        margin-bottom: 35px;
    }

    .nav__link:hover::before {
        width: 15px;
        height: 15px;
        margin-right: 10px;
    }

    .nav__logo {
        display: none;
    }

    .menu__hamburguer {
        top: 45px;
        right: 25px;
    }

    .open .hamburguer__bar {
        transform-origin: 13px 2px;
    }

    .hamburguer__bar {
        height: 3px;
    }
}

