/* Styles organization index */

    /* Global styles */
    /* Typography styles */
    /* General styles */
    /* Animation styles */
    /* 1500px media queries */
    /* 1390px media queries */
    /* 1100px media queries */
    /* 900px media queries */
    /* 750px media queries */
    /* 550px media queries */

/* ------------------------- */



/* ------------ Global styles ------------ */

/* Variables */

:root {
    --primary-clr: #83907C;
    --secondary-clr: #000000;
    --accent-clr: #DBCBAA;
    --accent-clr2: #4E4641;
    --section-padding: 0 155px;
    --roaster-font: 'Roaster', serif;
    --Poppins-font: 'Poppins', sans-serif;
}

* {
    box-sizing: border-box;
}

body {
    margin: 0;
    padding: 0;
}

a {
    text-decoration: none;
    color: #000;
    cursor: pointer;
}

/* ------------ Typography styles ------------ */

@font-face {
    font-family: 'Roaster Regular';
    src: url('/fonts/Roaster-Regular.otf')  format('truetype'), /* Safari, Android, iOS */
}
@font-face {
    font-family: 'Roaster Light';
    src: url('/fonts/Roaster-Light.otf')  format('truetype'), /* Safari, Android, iOS */
}
@font-face {
    font-family: 'Roaster Bold';
    src: url('/fonts/Roaster-Bold.otf')  format('truetype'), /* Safari, Android, iOS */
}

.main__heading {
    font-family: var(--roaster-font);
    font-weight: 300;
    font-size: 122px;
    margin: 0;
}

.heading {
    font-family: var(--roaster-font);
    font-size: 62px;
    font-weight: 300;
    text-transform: uppercase;
}

.subheading {
    font-family: var(--roaster-font);
    font-size: 26px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 2px;
    color: #4E4641;
}

.subheading2 {
    font-family: var(--Poppins-font);
    font-size: 28px;
    font-weight: 400;
    color: #4E4641;
}

.text {
    font-family: var(--Poppins-font);
    font-size: 20px;
    font-weight: 300;
    letter-spacing: 1px;
    line-height: 140%;
}

@media screen and (max-width: 1390px) { 

    .main__heading {
        font-size: 92px;
    }

    .heading {
        font-size: 56px;
    }


}

@media screen and (max-width: 550px) {
    .heading {
        width: 100%;
        font-size: 34px;
    }
}


/* ------------ General styles ------------ */

.menu__hamburguer {
    position: fixed;
    top: 75px;
    right: 55px;
    z-index: 5;
    padding: 5px;
    cursor: pointer;
}

.hamburguer__bar {
    display: block;
    width: 30px;
    height: 4px;
    background-color: var(--primary-clr);
    transition: ease .4s;
}

.hamburguer__bar:first-child {
    margin-bottom: 5px;
}

/* Styles for when hamburguer is clicked (turns into x)*/

.open .hamburguer__bar{
    transform-origin: 12px 2px;
}

.open .hamburguer__bar:first-child {
    transform: rotate(45deg);
    margin-bottom: 0;
}

.open .hamburguer__bar:last-child {
    transform: rotate(-45deg);
}

/* ------ */

header {
    position: fixed;
    display: flex;
    height: 100vh;
    width: 100%;
    left: 0;
    top: 0;
    padding: 0 155px;
    transform: translateX(100%);
    background-color: #fff;
    z-index: 2;
}

.header__nav {
    height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    opacity: 0;
    transform: translateX(30px);
}

.nav__link {
    display: flex;
    align-items: center;
    width: 80%;
    font-family: var(--roaster-font);
    font-size: 64px;
    font-weight: 300;
    transform: translate(-30px);
    transition: ease 1s;
    
}

.nav__link:not(:last-child) {
    margin-bottom: 15px;
}

.nav__link:hover {
    transform: scale(1.05);
}

.nav__link::before {
    content: '';
    display: inline-block;
    width: 0;
    height: 30px;
    background-color: var(--primary-clr);
    border-radius: 100%;
    margin: auto 0;
    margin-right: 0px;
    transition: ease .5s;
}

.nav__link:hover::before {
    content: '';
    display: inline-block;
    width: 30px;
    height: 30px;
    background-color: var(--primary-clr);
    margin-right: 30px;
}

.nav__logo {
    width: 50%;
    align-self: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    opacity: 0;
    transform: translate(30px);
}

.nav__hashtag {
    font-family: var(--roaster-font);
    font-size: 42px;
    font-weight: 300;
}

/* Hero */

.hero {
    height: 100vh;
    background-image: url('/images/hero-bg.jpg');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: right;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: var(--section-padding);
    clip-path: polygon(0 0, 0 0, 0 100%, 0 100%);
}

.hero__message {
    align-self: left;
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
    padding-top: 85px;
    padding-bottom: 35px;
}

.hero__message p {
    font-family: var(--Poppins-font);
    font-weight: 300;
    font-size: 20px;
    text-transform: uppercase;
}

.hero__cta {
    margin: auto 0;
}

.hero__cta h1, .hero__cta span {
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* Animation achieved with gsap in index.js */
    opacity: 0;
    transform: translateY(100px);
}

.hero__cta span{
    color: var(--primary-clr);
}


.hero__cta button{
    padding: 15px 65px; 
    margin-top: 32px;
    background-color: var(--accent-clr);
    font-family: var(--Poppins-font);
    font-size: 20px;
    font-weight: 300;
    text-transform: uppercase;
    border: none;
    cursor: pointer;
    transition: ease .3s;
    opacity: 0;
}

.hero__cta button:hover {
    transform: scale(1.03);
    background-color: #c3b18d;
}

.hero__message p:last-child {
    margin-top: 85px;
}

.hero__slider{
    clip-path: polygon(100% 0, 100% 0, 100% 100%, 100% 100%); /* Animation achieved with gsap in index.js file */
}

.hero__slider img{
    float: right;
    width: 90%;
    height: auto;
}

.story {
    height: 100vh;
    padding: 100px 155px 100px 155px;
    display: flex;
}

.story__imgContainer {
    width: fit-content;
}

.story__imgContainer img{
    padding: 20px;
    border: 2px solid #83907C;
    transform: translateX(-150%);
    transition: ease 1s;
}

.story__imgContainer h3{
    position: relative;
    padding: 20px 0;
    border: 2px solid #83907C;
    text-align: center;
    font-size: 24px;
    font-weight: 300;
    transform: translateX(-150%);
    transition: ease-in-out 1.1s;
}

.story__imgContainer h3::before{
    content: url('/images/rings.svg');
    display: flex;
    position: absolute;
    justify-content: center;
    width: 100%;
    top: -25px;
    transform: scale(.9);
}

.story__imgContainer .slide_in{
    transform: translateX(0);
}

.story__textContainer {
    padding: 0 135px;
}

.story__textContainer h2{
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    margin-bottom: 50px;
    opacity: 0;
    transition: ease .8s;
}

.tab__container {
    opacity: 0; /* This animation is achieved through locomotive scroll data-scroll-class and is defined in the animations section of this css file*/
    transition: ease 1s;
}

.tabs {
    width: fit-content;
    display: flex;
    border-bottom: #c3b18d 1px solid;
}

.tab_btn {
    border: none;
    background-color: #fff;
    padding: 20px 60px;
    font-family: var(--roaster-font);
    font-weight: 300;
    font-size: 18px;
    cursor: pointer;
    margin-bottom: 0;
}

.active {
    border-bottom: #c3b18d solid 2px;
}

.tab_panel {
    margin: 90px 0;
    height: 150px;
}

.tab_panel h3{
    font-family: var(--Poppins-font);
    font-size: 22px;
    font-weight: 500;
    color: #4E4641;
}

.tab_panel p{
    font-family: var(--Poppins-font);
    font-size: 22px;
    font-weight: 300;
    color: #000;
}

/* Location */

.location {
    padding: 100px 155px;
    display: flex;
}

.location__imgContainer {
    overflow: hidden;
}

.location__img {
    transform: scale(1.2);
    clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    transition: ease 1.5s;
}

.location__infoContainer {
    padding: 125px 75px;
    opacity: 0;
    transition: ease 1.5s;
}

.opacity_reveal {
    opacity: 1;
}

.location__infoContainer h2 {
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    opacity: 0;
    transition: ease .8s;
}

.location__address {
    font-family: var(--Poppins-font);
    font-size: 16px;
    margin-bottom: 80px;
}

.location__address p {
    margin: 3px 0;
}

.location__time h4 {
    margin: 3px 0;
}

.gallery {
    padding: 100px 155px;
}

.gallery h2{
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    transition: ease .8s;
}

.gallery__images {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(2, 30vw);
    grid-gap: 15px;
}

.gallery__item {
    overflow: hidden;
    position: relative;
    background-color: #000;
    cursor: pointer;
    clip-path: polygon(0 0, 0 0, 0 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
}

.gallery__item span {
    position: absolute;
    left: 30px;
    bottom: 50px;
    z-index: 1;
    font-family: var(--roaster-font);
    font-size: 24px;
    font-weight: 300;
    color: #fff;
    opacity: 0;
    transition: ease-in-out .5s;
}

.gallery__img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transition: ease-in-out .5s;
}

.gallery__item:hover .gallery__img{
    opacity: 0.8;
    transform: scale(1.03);
}

.gallery__item:hover span {
    opacity: 1;
}

.gallery__item--1 {
    grid-column-start: 1;
    grid-column-end: 1;
    grid-row-start: 1;
    grid-row-end: 1;
    transition: ease 1s;
}

.gallery__item--1 img {
    object-position: left;
}

.gallery__item--2 {
    grid-column-start: 2;
    grid-column-end: 4;
    grid-row-start: 1;
    grid-row-end: 1;
    transition: ease 1.2s;
}

.gallery__item--3 {
    grid-column-start: 4;
    grid-column-end: 5;
    grid-row-start: 1;
    grid-row-end: 1;
    transition: ease 1.4s;
}

.gallery__item--4 {
    grid-column-start: 1;
    grid-column-end: 3;
    grid-row-start: 2;
    grid-row-end: 3;
    transition: ease 1s;
}

.gallery__item--4 img {
    object-position: top;
}

.gallery__item--5 {
    grid-column-start: 3;
    grid-column-end: 4;
    grid-row-start: 2;
    grid-row-end: 3;
    transition: ease 1.2s;
}

.gallery__item--6 {
    grid-column-start: 4;
    grid-column-end: 5;
    grid-row-start: 2;
    grid-row-end: 3;
    transition: ease 1.4s;
}

.events {
    padding: 50px 155px;
}

.events h2 {
    text-align: center;
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    transition: ease .8s;
}

.timeline {
    position: relative;
    margin: 0px auto;
}
  
.timeline::after {
    content: '';
    position: absolute;
    width: 4px;
    height: 0; /* This animations (timeline line) are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    background-color: var(--accent-clr);
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -2px;
    transition: ease-in-out 3s;
}

.container {
    padding: 80px 40px 80px 200px;
    position: relative; 
    width: 50%;
}

.right {
    padding: 40px 100px 40px 50px;
}
  
/* The lines on the timeline */
.left::after {
    content: '';
    position: absolute;
    width: 0px; /* This animations (timeline line) are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    height: 3px;
    right: 0px;
    background-color: var(--accent-clr);
    top: 125px;
    z-index: 1;
    transition: ease-in-out 2s;
}

.right::after {
    content: '';
    position: absolute;
    width: 0px; /* This animations (timeline line) are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    height: 3px;
    left: 0px;
    background-color: var(--accent-clr);
    top: 125px;
    z-index: 1;
    transition: ease-in-out 2s;
}

.content {
    opacity: 0;
    transition: ease 1.5s;
}

.content span{
    font-family: var(--Poppins-font);
    font-weight: 400;
    font-size: 22px;
    color: #4E4641;
}

/* Place the container to the left */
.left {
    left: 0;
}
  
  /* Place the container to the right */
.right {
    left: 50%;
}

.hotels {
    padding: 100px 155px;
    margin: 20px 0;
}

.hotels h2{
    clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0% 100%); /* This animations are achieved through locomotive scroll data-scroll-class and are defined in the animations section of this css file*/
    margin-bottom: 10px;
    opacity: 0;
    transition: ease .8s;
    text-align: center;
}

.hotels p{
    text-align: center;
}

.hotels_container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    padding: 100px 0;
}

.hotel__imgContainer {
    overflow: hidden;
    position: relative;
}

.hotel__img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
}

/* ------------ Animation styles ------------ */

    /* This animations are achieved using locomotive scroll's data-scroll-class when in view */

.story__textContainer .title_reveal { /* .story__textContainer h2 */
    clip-path : polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%);
    opacity: 1;
}

.opacity_reveal { /* story tab container */
    opacity: 1;
}

.location__imgContainer .img_reveal { /* .location__img */
    clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
}

.location__infoContainer .title_reveal{ /* .location__infoContainer h2 */
    clip-path : polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%);
    opacity: 1;
}

.gallery .title_reveal{ /* .gallery h2 */
    clip-path : polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%);
}

.gallery__images .img_reveal { /* .gallery__item */
    clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
}

.events .title_reveal { /* .events h2 */
    clip-path : polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%);
}

.line_reveal .timeline::after { /* .timeline::after */
    height: 100%;
}

.timeline .reveal::after { /* .right::after and .left::after */
    width: 55px;
}

.reveal .content { /* .content */
    opacity: 1;
}

.hotels .title_reveal { /* .hotels h2 */
    clip-path : polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%);
    opacity: 1;
}

/* ------------ Media queries - Smaller Desktop screens ------------ */

@media screen and (max-width: 1500px) {
    :root {
        --section-padding: 0 95px;
    }

    .story {
        padding: 100px 95px;
    }

    .story__textContainer {
        padding: 0 25px 0 85px;
    }

    .story__textContainer h2{
        margin-bottom: 20px;
    }

    .location {
        padding: 100px 95px;
        display: flex;
    }

    .gallery {
        padding: 100px 95px;
    }
}

/* ------------ Media queries - Laptop screens ------------ */

@media screen and (max-width: 1390px) {
    header {
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }

    .header__nav {
        z-index: 3;
    }

    .hero__slider img {
        width: 80%;
    }

    
    .hero__cta button{
        padding: 15px 05px; 
        width: 25vw
    }

    
    .nav__link {
        font-size: 82px;
        width: 100%;
    }
    
    .nav__link:not(:last-child) {
        margin-bottom: 55px;
    }
    
    .nav__logo {
        position: absolute;
        z-index: 1;
        opacity: .5;
    }

    .story {
        padding: 100px 55px;
        height: auto;
    }

    .story__textContainer {
        padding: 25px 25px 25px 85px;
    }

    .story__textContainer h2{
        margin-bottom: 10px;
    }

    .tab_btn {
        padding: 20px 40px;
    }

    .location {
        padding: 100px 55px;
    }

    .gallery     {
        padding: 100px 55px;
    }

    .events {
        padding: 100px 55px;
    }

    .container {
        padding: 80px 40px 80px 50px;
        position: relative; 
        width: 50%;
    }

    .right {
        padding: 40px 0px 40px 50px;
    }


}

/* ------------ Media queries - Large Tablet screens ------------ */

@media screen and (max-width: 1100px) {
    header {
        height: auto;
        width: 100%;
        padding: 0 95px;
        z-index: 3;
        flex-direction: column;
        background-image: url('../images/logo.jpg');
        background-repeat: no-repeat;
        background-position: center;
    }

    .nav__link {
        font-size: 62px;
    }

    .nav__logo {
        display: none;
    }
    
    .nav__hashtag {
        display: none;
    }

    /* Hero */

    .hero {
        width: 100%;
        background-image: url('/images/tablet-hero-bg.jpg');
        background-size: cover;
        flex-direction: column;
        padding: 0 95px;
    }

    .hero__message {
        align-self: center;
        padding: 0;
        z-index: 2;
        text-align: center;
    }

    .hero__message p:last-child {
        margin-top: 5px;
    }

    .hero__cta {
        margin: 70px 0;
        margin-bottom: 95px;
    }

    .hero__cta h1 {
        color: #fff;
    }

    .hero__cta button {
        width: 400px;
        padding: 20px 5px;
    }

    .hero__slider {
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        z-index: 1;
    }

    .hero__slider img {
        display: none;
    }

    .story {
        flex-direction: column;
        height: 100%;
        padding: 100px 45px;
    }

    .story__imgContainer {
        margin: 0 auto;
    }

    .story__textContainer {
        padding: 0 45px;
    }

    .tabs {
        flex-direction: column;
        width: 100%;
        border: none;
    }

    .tab_btn {
        text-align: center;
        border-bottom: #c3b18d 1px solid;
        height: 80px;
    }

    .active {
        border-bottom: #c3b18d solid 3px;
    }

    .tab_panel {
        height: auto;
        margin: 50px 0;
    }

    .location {
        flex-direction: column;
        padding: 50px 45px;
    }

    .location__imgContainer {
        margin: 0 auto;
    }

    .location__infoContainer {
        padding: 50px 40px;
    }

    .gallery {
        height: auto;
        padding: 50px 95px;
    }

    .gallery__item:hover .gallery__img{
