        transform: scale(1)
    }
    
    .gallery__item span {
        opacity: 1;
    }

    .events {
        padding: 100px 45px;
    }

}

/* ------------ Media queries - Tablet screens ------------ */

@media screen and (max-width: 900px) {

    .story__textContainer {
        padding: 0 0px;
    }

    .location__infoContainer {
        padding: 50px 0px;
    }

    .location__img {
        width: 100%;
    }

    .gallery {
        padding: 50px 45px;
    }

    .gallery__images {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        grid-template-rows: repeat(3, 40vh);
        grid-gap: 15px;
    }
    
    .gallery__item--1 {
        grid-column-start: 1;
        grid-column-end: 2;
        grid-row-start: 1;
        grid-row-end: 2;
    }
    
    .gallery__item--1 img {
        object-position: left;
    }
    
    .gallery__item--2 {
        grid-column-start: 2;
        grid-column-end: 4;
        grid-row-start: 1;
        grid-row-end: 2;
    }
    
    .gallery__item--3 {
        grid-column-start: 1;
        grid-column-end: 3;
        grid-row-start: 2;
        grid-row-end: 3;
    }

    .gallery__item--4 {
        grid-column-start: 3;
        grid-column-end: 4;
        grid-row-start: 2;
        grid-row-end: 3;
    }

    .gallery__item--5 {
        grid-column-start: 1;
        grid-column-end: 3;
        grid-row-start: 3;
        grid-row-end: 4;
    }

    .gallery__item--6 {
        grid-column-start: 3;
        grid-column-end: 4;
        grid-row-start: 3;
        grid-row-end: 4;
    }
    


}

/* ------------ Media queries - Bigger phone screens or small tablets ------------ */

@media screen and (max-width: 750px) { 
    .container {
        padding: 80px 30px 0px 20px;
    }
    
    .right {
        padding: 40px 0px 40px 40px;
    }

    .left::after {
        content: '';
        position: absolute;
        width: 55px;
        height: 3px;
        right: 0px;
        background-color: var(--accent-clr);
        top: 95px;
        z-index: 1;
    }
    
    .right::after {
        content: '';
        position: absolute;
        width: 55px;
        height: 3px;
        left: 0px;
        background-color: var(--accent-clr);
        top: 65px;
        z-index: 1;
    }
}

/* ------------ Media queries - Smartphone screens ------------ */

@media screen and (max-width: 550px) {
    header {
        padding: 0 65px;
        background-image: url('/images/logo.jpg');
        background-size: 50%;
        background-repeat: no-repeat;
        background-position: center center;
    }

    .nav__link {
        font-size: 32px;
    }

    .nav__link:not(:last-child) {
        margin-bottom: 35px;
    }

    .nav__link:hover::before {
        width: 15px;
        height: 15px;
        margin-right: 10px;
    }

    .nav__logo {
        display: none;
    }

    .menu__hamburguer {
        top: 45px;
        right: 25px;
    }

    .open .hamburguer__bar {
        transform-origin: 13px 2px;
    }

    .hamburguer__bar {
        height: 3px;
    }

    .hero {
        background-image: url('/images/tablet-hero-bg.jpg');
        background-size: cover;
        background-position: center center;
    }

    .hero__message {
        height: 100vh;
    }

    .hero__cta {
        margin: auto;
    }

    .hero__cta h1 {
        font-size: 80px;
    }

    .hero__cta button{
        width: 300px;
        padding: 15px; 
        margin-top: 32px;
        font-size: 16px;
    }

    .hero__message p:first-child {
        margin: auto;
    }

    .hero__message p:last-child {
        margin: auto;
    }

    .story {
        height: 100%;
        padding: 70px 20px;
    }
    
    .story__imgContainer {
        width: fit-content;
    }
    
    .story__imgContainer img{
        width: 100%
    }
    
    .story__imgContainer h3{
        font-size: 20px;
    }

    .story__textContainer {
        height: 100%;
        padding: 0;
    }

    .tabs {
        margin-bottom: 100px;
    }

    .tab_panel {
        font-size: 16px;
    }

    .location {
        padding: 40px 20px;
    }

    .location__infoContainer {
        padding: 35px 0px;
    }
 
    .gallery {
        padding: 50px 20px;
    }

    .gallery h2 {
        margin: 0;
    }

    .gallery__images {
        padding: 0;
        display: grid;
        grid-template-columns: repeat(1, 1fr);
        grid-template-rows: repeat(6, 40vh);
        grid-gap: 15px;
    }
    
    .gallery__item {
        grid-column-start: 1;
        grid-column-end: 2;
    }

    .gallery__item--1 {
        grid-row-start: 1;
        grid-row-end: 2;
    }
    
    .gallery__item--1 img {
        object-position: left;
    }
    
    .gallery__item--2 {
        grid-row-start: 2;
        grid-row-end: 3;
    }
    
    .gallery__item--3 {
        grid-row-start: 3;
        grid-row-end: 4;
    }

    .gallery__item--4 {
        grid-row-start: 4;
        grid-row-end: 5;
    }

    .gallery__item--5 {
        grid-row-start: 5;
        grid-row-end: 6;
    }

    .gallery__item--6 {
        grid-row-start: 6;
        grid-row-end: 7;
    }

    .events {
        padding: 50px 20px;
    }

    .container {
        width: auto;
        padding: 80px 0px 40px 90px;
        position: relative; 
    }

    .left::after {
        content: '';
        position: absolute;
        width: 0px;
        height: 3px;
        left: 0px;
        background-color: var(--accent-clr);
        top: 125px;
        z-index: 1;
        transition: ease-in-out 2s;
    }

    .timeline {
        margin: 50px 20px;
    }

    .timeline::after {
        left: 0%;
    }

    .left {
        left: 0;
    }
    
    .right {
        width: auto;
        padding: 10px 0px 40px 45px;
        left: 0;
    }
}
